$(document).ready(function(){ 
		$(".button-collapse").sideNav();   
		$("#token").change(function(){
        $("#submitButton").attr("href", "profile.html?token="+ $("#token").val());
    });
		//submit button click handler function.
		$("#submitButton").click(function(){
			if ($("#token").val()=="") {
				 Materialize.toast('Enter access token!', 3000);
			}
			else {
				$("#submitButton").attr("href", "profile.html?token="+ $("#token").val());
			}
		});//end submit button click function.
    }); 